// Forked from https://github.com/p-sam/electron-prompt
// This library has been modified to accept multiple inputs.

const electron = require('electron');

const BrowserWindow = electron.BrowserWindow || electron.remote.BrowserWindow;
const ipcMain = electron.ipcMain || electron.remote.ipcMain;
const url = require('url');
const path = require('path');

function electronPrompt(options, parentWindow) {
    return new Promise((resolve, reject) => {
        const id = `${new Date().getTime()}-${Math.random()}`;

        const opts = Object.assign(
            {
                width: 370,
                height: 155,
                resizable: false,
                title: 'Orb',
                alwaysOnTop: true,
                icon: null
            },
            options || {}
        );

        let promptWindow = new BrowserWindow({
            width: opts.width,
            height: opts.height,
            resizable: opts.resizable,
            parent: parentWindow,
            skipTaskbar: true,
            alwaysOnTop: opts.alwaysOnTop,
            useContentSize: true,
            modal: Boolean(parentWindow),
            title: opts.title,
            icon: opts.icon
        });

        promptWindow.setMenu(null);

        const getOptionsListener = event => {
            event.returnValue = JSON.stringify(opts);
        };

        const cleanup = () => {
            if (promptWindow) {
                promptWindow.close();
                promptWindow = null;
            }
        };

        const postDataListener = (event, value) => {
            resolve(value);
            event.returnValue = null;
            cleanup();
        };

        const unresponsiveListener = () => {
            reject(new Error('Window was unresponsive'));
            cleanup();
        };

        const errorListener = (event, message) => {
            reject(new Error(message));
            event.returnValue = null;
            cleanup();
        };

        const consoleListener = (event, message) => {
            console.log(message);
        }

        ipcMain.on('prompt-get-options:' + id, getOptionsListener);
        ipcMain.on('prompt-post-data:' + id, postDataListener);
        ipcMain.on('prompt-error:' + id, errorListener);
        ipcMain.on('prompt-console:' + id, consoleListener);
        promptWindow.on('unresponsive', unresponsiveListener);

        promptWindow.on('closed', () => {
            ipcMain.removeListener('prompt-get-options:' + id, getOptionsListener);
            ipcMain.removeListener('prompt-post-data:' + id, postDataListener);
            ipcMain.removeListener('prompt-error:' + id, postDataListener);
            ipcMain.removeListener('prompt-console:' + id, consoleListener);
            resolve(null);
        });

        const promptUrl = url.format({
            protocol: 'file',
            slashes: true,
            pathname: path.join(__dirname, 'page', 'prompt.html'),
            hash: id
        });

        promptWindow.loadURL(promptUrl);
    });
}

module.exports = electronPrompt;